binding.script:
 setprop("/sim/model/yokes", abs(getprop("sim/model/yokes") - 1));
if (getprop("/sim/model/yokes")) { 
  var yokestatus = "VISIBLE";
  } else {
  var yokestatus = "INVSIBLE";
 } 
setprop ("/sim/messages/copilot", "Yokes " ~ yokestatus);

 setprop("/sim/co-pilot", abs(getprop("sim/co-pilot") - 1));
if (getprop("/sim/co-pilot")) { 
  var cpstatus = "ON";
  } else {
  var cpstatus = "OFF";
 } 
setprop ("/sim/messages/copilot", "Co-Pilot " ~ cpstatus);
